import React, { useRef, useState, useEffect } from 'react';
import '../../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import MainNavbar from './MainNavbar.js';
import Player from './Player.js';

function Host(props) {
  const [queue, setQueue] = useState([]);
  const [video, setVideo] = useState("");
  const [loop, setLoop] = useState(true);
  const [playing, setPlaying] = useState(false);
  const [library, setLibrary] = useState({});
  console.log(props.library);

  props.socket.on("library", (data) => {
    setLibrary({
      "0001": {
        "code": "0001",
        "title": "나에게로 떠나는 여행",
        "artist": "버즈",
        "url": "https://www.youtube.com/watch?v=kJW5z5guFTQ"
      },
      "0002": {
        "code": "0002",
        "title": "남자를 몰라",
        "artist": "버즈",
        "url": "https://www.youtube.com/watch?v=YFF3PRNgRVM"
      },
      "0003": {
        "code": "0003",
        "title": "My Love (And)",
        "artist": "버즈",
        "url": "https://www.youtube.com/watch?v=k87uq7STZjI"
      }
    });
  })

  return (
    <>
      <MainNavbar queue={queue} library={library}/>
      <Player video={video} loop={loop} playing={playing}/>
    </>
  );
}

export default Host;
