import React from 'react';
import '../../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import {ButtonGroup, Button} from 'react-bootstrap';

function UserPlaybackOptions(props) {
  return (
    <>
    <ButtonGroup size="lg" className="mb-2">
      <Button onClick={props.skipSong}> Skip </Button>
      <Button onClick={() => {
        if (!props.stop) {
          props.stopSong();
        }
      }}> Stop </Button>
      <Button onClick={props.playSong}> Play </Button>
    </ButtonGroup>
      <a className="dropdown-item" onClick={props.skipSong}> Skip </a>
      <a className="dropdown-item" onClick={() => {
        if (!props.stop) {
          props.stopSong();
        }
      }}> Stop </a>
      <a className="dropdown-item" onClick={props.playSong}> Play </a>
    </>
  );
}

  export default UserPlaybackOptions
