import React, { useRef, useState, useEffect } from 'react';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button, Tab, Nav, InputGroup, FormControl} from 'react-bootstrap';
//Do i need to include this line in every file that uses socket even when ive passed through props?
var socketClient = require('socket.io-client');

function App() {
  const socket = useRef(null);
  const role = useRef(null);
  const flag = useRef(true);

  // Are the on functions stored in the socket so they move with it when sent as prop? or do i need to implement in every page?
  function connect(role, ip) {
    if (!ip) {
      alert("IP address is needed");
    }
    socket.current = socketClient(ip+':4000');
    // if user: use hostIP:4000
    socket.current.on("connection", user => {
      role.current = user;
      console.log(role.current);
      console.log(socket.current.id);
    })
    socket.current.on("close", () => {
      flag.current = false;
      socket.current.close();
      console.log("Disconnected from server");
    })
    return () => {
      if (flag)
        socket.current.close();
    }
  }

  return (
    <div className="App">
      <div id="connectPrompt" className="align-items-center justify-content-center text-center" style={{position: 'absolute', left: '50%', top: '50%', transform: 'translate(-50%, -50%)'}}>
        <Row>
          <h2 className="align-items-center" style={{padding: "0"}}> Connect as Host or User? </h2>
        </Row>
        <Row>
          <Tab.Container defaultActiveKey="host">
            <Col id="connectTabContainer">
              <Row>
                <Nav variant="pills" fill style={{width: "100%"}}>
                  <Nav.Item>
                    <Nav.Link className="rounded-0" eventKey="host"> Host </Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link className="rounded-0" eventKey="user"> User </Nav.Link>
                  </Nav.Item>
                </Nav>
              </Row>
              <Row style={{padding: "15px 0px"}}>
                <Tab.Content style={{ width:"100%"}}>
                  <Tab.Pane eventKey="host">
                    <Button className="rounded-0" block onClick={(e) => {
                      connect("host", "localhost");
                    }}> Create Room </Button>
                  </Tab.Pane>
                  <Tab.Pane eventKey="user">
                    <InputGroup className="mb-3">
                      <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1">Host IP</InputGroup.Text>
                      </InputGroup.Prepend>
                      <FormControl
                        id="hostIP"
                        placeholder="192.168.1.1"
                        aria-label="Host IP"
                        aria-describedby="basic-addon1"
                      />
                      <Button className="rounded-0" block onClick={() => {
                        let hostIP = document.getElementById("hostIP").value;
                        connect("user", hostIP)
                      }}> Connect </Button>
                    </InputGroup>
                  </Tab.Pane>
                </Tab.Content>
              </Row>
            </Col>
          </Tab.Container>
        </Row>
      </div>
    </div>
  );
}

export default App;
