import React, { useRef, useState, useEffect } from 'react';
import '../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav } from 'react-bootstrap';
var socketClient = require('socket.io-client');

function App() {
  const socket = useRef(null);
  const role = useRef(null);
  const flag = useRef(true);


  useEffect( () => {
    console.log('useEffect');
    socket.current = socketClient('http://localhost:4000');
    socket.current.on("connection", user => {
      role.current = user;
      console.log(role.current);
      console.log(socket.current.id);
    })
    socket.current.on("close", () => {
      flag.current = false;
      socket.current.close();
      console.log("Disconnected from server");
    })
    return () => {
      if (flag)
        socket.current.close();
    }
  }, []);

  return (
    <div className="App">
      <Navbar bg="white" expand="lg" fixed="top" variant="light" className="shadow-sm py-3">
        <Navbar.Toggle aria-controls="navbarContent" aria-expanded="false" aria-label="Toggle navigation" className="navbar-toggler"><span className="navbar-toggler-icon"></span></Navbar.Toggle>
        <Navbar.Collapse id="navbarSupportedContent">
          <Nav className="mr-auto">
            <li className="nav-item">
              <div className="dropdown" >
                <a className="nav-link" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Queue </a>
                <div className="dropdown-menu" id="test" aria-labelledby="dropdownMenuButton">
                  <h1>Test</h1>
                </div>
              </div>
            </li>
            <li className="nav-item">
              <div className="dropdown" >
                <a className="nav-link" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Library </a>
                <div className="dropdown-menu" id="test" aria-labelledby="dropdownMenuButton">
                  <h1>Test</h1>
                </div>
              </div>
            </li>
            <li className="nav-item">
              <div className="dropdown" >
                <a className="nav-link" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Playback Options </a>
                <div className="dropdown-menu" id="test" aria-labelledby="dropdownMenuButton">
                  <h1>Test</h1>
                </div>
              </div>
            </li>
          </Nav>
          <Nav>
            <li className="nav-item">
              <div className="dropdown" >
                <a className="nav-link" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Options </a>
                <div className="dropdown-menu" id="test" aria-labelledby="dropdownMenuButton">
                  <h1>Test</h1>
                </div>
              </div>
            </li>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </div>
  );
}

export default App;
