import React from 'react';
import '../../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import MainNavbar from './MainNavbar.js';
import {Row, Tab, Col, Nav, InputGroup, FormControl, Button } from 'react-bootstrap';

function User(props) {

  return (
    <Tab.Container defaultActiveKey="library">
      <Nav variant="pills" fill style={{width: "100%"}}>
        <Nav.Item>
          <Nav.Link className="rounded-0" eventKey="queue"> Queue </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link className="rounded-0" eventKey="playback"> Playback </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link className="rounded-0" eventKey="add"> Add Song </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link className="rounded-0" eventKey="library"> Library </Nav.Link>
        </Nav.Item>
      </Nav>
      <Row>
        <Tab.Content style={{ width:"100%"}}>
          <Tab.Pane eventKey="queue">
            <Button className="rounded-0" block onClick={() => {
              props.connect("host", "localhost");
            }}> Create Room </Button>
          </Tab.Pane>
          <Tab.Pane eventKey="playback">
            <InputGroup className="mb-3">
              <InputGroup.Prepend>
                <InputGroup.Text id="basic-addon1">Host IP</InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                id="hostIP"
                placeholder="192.168.1.1"
                aria-label="Host IP"
                aria-describedby="basic-addon1"
              />
              <Button className="rounded-0" block onClick={() => {
                let hostIP = document.getElementById("hostIP").value;
                props.connect("user", hostIP)
              }}> Connect </Button>
            </InputGroup>
          </Tab.Pane>
        </Tab.Content>
      </Row>
    </Tab.Container>
  );
}

export default User;
