import React, { useMemo, useState } from 'react';
import $ from 'jquery';
import '../../node_modules/bootstrap/dist/js/bootstrap.min.js';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';


function Queue(props) {
  // console.log(props.queue);
  const [animated, setAnimated] = useState(false);

  let header = (
    <tr className="d-flex" key={"head"}>
      <th className="col" key={'Code'}>Code</th>
      <th className="col" key={'Title'}>Title</th>
      <th className="col" key={'Artist'}>Artist</th>
      <th className="col" key={'Button'}></th>
    </tr>
  );

  // let rows = useMemo(() => {
  //   var data = Object.values(props.queue);
  //   return data.map(function(song, i) {
  //     let time = Date.now();
  //     let key = song.code + time;
  //     return (
  //       <tr className="d-flex" key={"row" + i + key}>
  //         <td className="col" key={key}>{song.code}</td>
  //         <td className="col marquee" onMouseEnter={() => {console.log('entered'); setAnimated(true); console.log(animated)}} onAnimationEnd={() => {console.log('entered'); setAnimated(false)}} key={key + "title"}><span className={animated ? 'anim' : ''}>{song.title}</span></td>
  //         <td className="col" key={key + "artist"}>{song.artist}</td>
  //         <td className="col" key={"button"+song.code}><button className="btn btn-outline-dark" key={key} onClick={() => props.removeSong(i)}>Remove from Queue</button></td>
  //       </tr>
  //     );
  //   })
  // }, [props.queue, animated]);

  let rows = useMemo(() => {
    var data = Object.values(props.queue);
    return data.map(function(song, i) {
      let time = Date.now();
      let key = song.code + time;
      return (
        <tr className="d-flex" key={"row" + i + key}>
          <td className="col" key={key}>{song.code}</td>
          <td className="col marquee" key={key + "title"}><span id={key + "titleanim"} className='' onMouseEnter={(event) => {console.log('entered'); console.log(event.target.id); setAnimated(true); console.log(animated)}} onAnimationEnd={() => {console.log('entered'); setAnimated(false)}} >{song.title}</span></td>
          <td className="col" key={key + "artist"}>{song.artist}</td>
          <td className="col" key={"button"+song.code}><button className="btn btn-outline-dark" key={key} onClick={() => props.removeSong(i)}>Remove from Queue</button></td>
        </tr>
      );
    })
  }, [props.queue, animated]);

  const table = (
    <table className="table table-hover table-sm">
      <thead>
        {header}
      </thead>
      <tbody className="scroll">
        {rows}
      </tbody>
    </table>
  );

  return (
    <div className="table-fixed">
      {table}
    </div>
  );
}

export default Queue;
