const express = require('express');
const http = require('http');
var socketIo = require('socket.io');

const port = process.env.PORT || 4001;

const app = express();

const server = http.createServer(app);

const io = socketIo(server);


// const pool = require('./controllers/mongodbpool');
// pool.connectToServer( function (err) {
  const index = require("./routes/index.routes.js")(io);
  app.use('/', index);
// });

var playerJson = {};

io.on("connection", (socket) => {
  if (Object.keys(playerJson).length == 0) {
    playerJson[socket] = "Host";
  }
  else if (Object.keys(playerJson).length < 4) {
    for (var i=1; i < 4; i++) {
      if (!(i in Object.values(playerJson))) {
        playerJson[socket] = i;
        socket.emit('user', playerJson[socket]);
      }
      break;
    }
  }
  else {
    socket.disconnect();
  }
})

io.on("disconnect", (socket) => {
  if (playerJson[socket] == "Host") {
    Object.keys(playerJson).forEach((key) => {
      key.disconnect();
    })
    playerJson = {};
  }
  else {
    socket.disconnect();
    delete playerJson[socket];
  }
})

server.listen(port, () => {
  // Tell us what port it's running on
  console.log("Karaoke server started on " + port)
});

const getApiAndEmit = "TODO";

export default App;
